void readCoeffs(double* array_out, int filter_num, int coeff_num, double* template_array )
{
  memcpy(array_out,template_array,sizeof(double)*coeff_num);
}

/* gateway function */
void mexFunction( int nlhs, mxArray *plhs[],
        int nrhs, const mxArray *prhs[] )
{
  /* here we should do some error checking
   * nlhs == 1 and nrhs == 2  */
  (void) nlhs;
  (void) nrhs;
  (void) plhs;
  (void) prhs;
  if ((nlhs != 2 && nrhs != 3)&&(nlhs != 4 && nrhs != 5))
  {
    mexPrintf("Wrong number of inputs and outputs \n");
    plhs[0]=0;
    return;
  }
  /* the magical filter index */
  int index;
  /* some arrays to pass to mexCall */
  mxArray* aFilterInput1[4];
  mxArray* tmpOut1[2];
  index = (int)mxGetScalar(prhs[0]);
  
  if (index>103 || index<100)
  {
    mexPrintf("Wrong filter index range \n");
    return;
  }
  // map index to range 0 to 3
  index-=100;
  
#if DEBUG
  mexCallMATLAB(0,plhs,1,&prhs[0], "disp");
  mexCallMATLAB(0,plhs,1,&prhs[1], "disp");
#endif
  aFilterInput1[0]=mxCreateDoubleMatrix(num_b1[index],1,mxREAL);
  aFilterInput1[1]=mxCreateDoubleMatrix(num_a1[index],1,mxREAL);
  readCoeffs(mxGetPr(aFilterInput1[0]),index,num_b1[index],coeff_b1[index]);
  readCoeffs(mxGetPr(aFilterInput1[1]),index,num_a1[index],coeff_a1[index]);
  /* Just pass the data as 3rd argument */
  aFilterInput1[2] = const_cast<mxArray*>(prhs[1]);
  aFilterInput1[3] = const_cast<mxArray*>(prhs[2]);
  mexCallMATLAB(2,tmpOut1,4,aFilterInput1, "filter");
  plhs[0]=tmpOut1[0];
  plhs[1]=tmpOut1[1];
  mxDestroyArray(aFilterInput1[0]);
  mxDestroyArray(aFilterInput1[1]);
  return;
}
